refsection.programlisting:

#include <stack-c.h>

int sci_check_properties(char * fname)
{
  int m1, n1, l1;
  int m2, n2, l2;

  CheckRhs(2,2);

  GetRhsVar(1, "d", &m1, &n1, &l1);
  GetRhsVar(2, "d", &m2, &n2, &l2);

  CheckSameDims(1,2,m1,n1,m2,n2); // Check that both vectors have the same size

  return 0;
}
