refsection.programlisting:

#include "api_scilab.h"
int write_string(char *fname,unsigned long fname_len)
{
	SciErr sciErr;
	//情報変数 : 文字列の行列 2 x 3
	int iRows		= 2;
	int iCols		= 3;
	char** pstData	= NULL;
	//新規変数にデータを保存
	char string11[]	= "may";
	char string21[]	= "be";
	char string12[]	= "the";
	char string22[]	= "with";
	char string13[]	= "puffin";
	char string23[]	= "you";
	//新規配列用にメモリ確保
	pstData			= (char**)malloc(sizeof(char*) * iRows * iCols);
	//"main" 配列にデータアドレスをコピー
	pstData[0]		= string11;
	pstData[1]		= string21;
	pstData[2]		= string12;
	pstData[3]		= string22;
	pstData[4]		= string13;
	pstData[5]		= string23;
	//変数を作成
	sciErr = createMatrixOfString(pvApiCtx, nbInputArgument(pvApiCtx) + 1, iRows, iCols, pstData);
	if(sciErr.iErr)
	{
		printError(&sciErr, 0);
		return 0;
	}
	//コンテナを解放
	free(pstData);
	//確保された変数を左辺に保存
    AssignOutputVariable(pvApiCtx, 1) = nbInputArgument(pvApiCtx) + 1;
	return 0;
}


a_ref = "may the puffin be with you ";
b = [];
a = write_string();
for i=1:size(a,"r")
    for j=1:size(a,"c")
        b = b + a(i,j);
        b = b + " ";
    end
end
if b <> a_ref then error("failed"), end
