refsection.programlisting:
function foo(a)
         a=a+1
         b=resume(a)
         c=52
endfunction

foo(42);
assert_checkequal(b,43);
// c n'exite pas

// Avec plusieurs appels de fonctions
function foo1()
  a=1;
  b=2;
  c=3;
  [x,y,z]=resume(a, b, c)
endfunction

function foo2()
  foo1()
  x, y, z // Déclarés
endfunction

foo2()
// x y z n'existent pas ici
