refsection.programlisting:
function foo(a)
         a=a+1
         b=resume(a)
         c=52
endfunction

foo(42);
assert_checkequal(b,43);
// c не существует

// при наличии нескольких вызовов функции
function foo1()
  a=1;
  b=2;
  c=3;
  [x, y, z] = resume(a, b, c)
endfunction

function foo2()
  foo1()
  x, y, z // объявлены
endfunction

foo2()
// x, y и z здесь не существуют
