refsection.programlisting:

s = struct("field_1",123,"field_2",456,"field_4",789)
s1 = struct("field_1",123,"field_2",456,"field_3",789)

// Test pour un nom de champ unique :
isfield( s , "field_1"  )

// Test simultané pour plusieurs noms de champs :
isfield( s , [ "field_1" "field_2" ; "field_3" "field_4" ] )

// Test sur une liste typée tlist:
t = tlist(["test" "t" "n","","p"], "Hello", %pi, "anonymous", (1-%z)^3)
isfield( t, ["b" "t" ""])  // =>  F  T  T  (la présence de champs anonymes peut être détectée)

// Test sur une liste de structures
isfield(list(s,s1), "field_4")
isfield(list(s,s1), "field_3")

// Test sur une list composée d'une structure et d'une tlist
isfield(list(s,t), "field_1")
isfield(list(s,t), "n")
