m = 50000 ; 
n = 10;
X = grand(m,1,"def");
s = linspace(0,1,n)';
[ind, occ] = dsearch(X, s);
e = 1/(n-1)*ones(1,n-1);
scf() ; 
plot(s(1:n-1), occ/m,"bo");
plot(s(1:n-1), e,"r-");
legend(["Эксперимент","Ожидание"]);
xtitle("Случайные равновероятные числа","X","P(X)");


N = 8 ; 
p = 0.5; 
m = 50000;
X = grand(m,1,"bin",N,p); 
s = (0:N)';
[ind, occ] = dsearch(X, s, "d");
Pexp = occ/m; 
Pexa = binomial(p,N);
scf() ; 
plot(s,Pexp,"bo");
plot(s,Pexa',"r-");
xtitle("Биномиальное распределение B(8,0.5)","X","P(X)");
legend(["Эксперимент","Ожидание"]);



// определяем основные функции Эрмита
function y=Ll(t,k,x)
  // Lagrange left on Ik
  y=(t-x(k+1))./(x(k)-x(k+1))
endfunction
function y=Lr(t,k,x)
  // Lagrange right on Ik
  y=(t-x(k))./(x(k+1)-x(k))
endfunction
function y=Hl(t,k,x)
  y=(1-2*(t-x(k))./(x(k)-x(k+1))).*Ll(t,k,x).^2
endfunction
function y=Hr(t,k,x)
  y=(1-2*(t-x(k+1))./(x(k+1)-x(k))).*Lr(t,k,x).^2
endfunction
function y=Kl(t,k,x)
  y=(t-x(k)).*Ll(t,k,x).^2
endfunction
function y=Kr(t,k,x)
  y=(t-x(k+1)).*Lr(t,k,x).^2
endfunction

x = [0 ; 0.2 ; 0.35 ; 0.5 ; 0.65 ; 0.8 ;  1];
y = [0 ; 0.1 ;-0.1  ; 0   ; 0.4  ;-0.1 ;  0];
d = [1 ; 0   ; 0    ; 1   ; 0    ; 0   ; -1];
X = linspace(0, 1, 200)';
ind = dsearch(X, x);

// построение кривой
Y = y(ind).*Hl(X,ind) + y(ind+1).*Hr(X,ind) + d(ind).*Kl(X,ind) + d(ind+1).*Kr(X,ind);
scf();
plot(X,Y,"k-");
plot(x,y,"bo")
xtitle("кусочный полином Эрмита");
legend(["Полином","Данные"]);
// Примечание : можете проверить, добавив это :
