set(gcf(),"color_map",coolcolormap(64))
// colors according to x values 
c = x
// create 2D scatter plot
scatter(x,y,s,c)

// x and y initialisation
x = linspace(0,2,200)
y = exp(-x).*cos(10*x) + 0.2*rand(1,length(x))
