refsection.programlisting:
function t = F(x, y, z)
    t = 1/2*x^2 + y^2 - z^2 - 1;
endfunction

x_range = linspace(-5, 5, 10);
y_range = linspace(-5, 5, 10);
z_range = linspace(-6, 6, 15);

// Draw the curve in the [-5 5] x [-5 5] x [-6 6] range
implicitplot3d(F, x_range, y_range)

