refsection.programlisting:

plot2d(1:10,1:10,1,"020")
// horizontal axis 
drawaxis(x=2:7,y=4,dir='u',ticks='v')
// horizontal axis on top of the frame
drawaxis(x=2:7,dir='u',ticks='v') 
// horizontal axis at the bottom of the frame
drawaxis(x=2:7,dir='d',ticks='v') 

// horizontal axis given by a range 
drawaxis(x=[2,7,3],y=4,dir='d',ticks='r') 

// vertical axis 
drawaxis(x=4,y=2:7,dir='r',ticks='v')
drawaxis(x=2,y=[2,7,3],dir='l',ticks='r')
drawaxis(y=2:7,dir='r',ticks='v')
drawaxis(y=2:7,dir='l',ticks='v')

// horizontal axis with strings displayed at ticks positions
drawaxis(x=2:7,y=8,dir='u',ticks='v',val='A'+string(1:6));
// vertical axis with strings displayed at ticks positions
drawaxis(x=8,y=2:7,dir='r',ticks='v',val='B'+string(1:6));

// horizontal axis given with a 'i' range. 
drawaxis(x=[2,5,0,3],y=9,dir='u',ticks='i');
drawaxis(x=9,y=[2,5,0,3],dir='r',ticks='i',sub_int=5);

// horizontal axis again 
drawaxis(x=2:7,y=4,dir='u',ticks='v',fontsize=10,textcolor=9,tickscolor=7,seg=0,sub_int=20)

