refsection.programlisting:
x=linspace(0,1,30)';
y=sin(4*x.^3)/2;
clf();a=gca();a.isoview='on';
plot(x,y)
pt=[0.7 0.3];
plot(pt(1),pt(2),'xb')
[d,ptp,ind,c]=orthProj([x y],pt);
plot(x(ind:ind+1),y(ind:ind+1),'+r')
xpoly([pt(1);ptp(1)],[pt(2);ptp(2)])
e=gce();e.polyline_style=4;e.arrow_size_factor = 1.5;

pt=[0.75 -0.3];
plot(pt(1),pt(2),'xb')
[d,ptp,ind,c]=orthProj([x y],pt);
plot(x(ind:ind+1),y(ind:ind+1),'+r')
xpoly([pt(1);ptp(1)],[pt(2);ptp(2)])
e=gce();e.polyline_style=4;e.arrow_size_factor = 1.5;

