refsection.programlisting:

plot2d([0, 1], [0, 1], 0);
// Only valid from Scilab 5.2
mathml = "<mrow>" +.. 
            "<mfrac>" +..
                "<mrow>" +.. 
                    "<mi> d </mi>" +.. 
                    "<mi> y </mi>" +..  
                "</mrow>" +..
                "<mrow>" +..
                    "<mi>d</mi>" +..
                    "<mi>x</mi>" +..
                "</mrow>" +..
            "</mfrac>" +..
                "<mo> = </mo>" +..
            "<mfrac>" +..
                "<mn> 1 </mn>" +..
                "<msup>" +..
                    "<mi> y </mi>" +..
                    "<mn> 2 </mn>" +..
                "</msup>" +..
            "</mfrac>" +..
         "</mrow>";
str=["SCILAB", "can write LaTeX :", "$\frac{abc}{xyz}$"; "or", "MathML :", mathml]; 
r2 = xstringl(0.2, 0.4, str, 10, 4);
xrects([r2(1) r2(2)+r2(4) r2(3) r2(4)]');
xstring(r2(1), r2(2), str);
txt = gce();
txt.font_size = 4;
txt.font_style = 10;

