refsection.programlisting:
x=sin(2*%pi*(0:4)/5);
y=cos(2*%pi*(0:4)/5);
plot2d(0,0,-1,"010"," ",[-2,-2,2,2]);
xset("color",5);
xfpoly(x,y);

e=gce(); // entité courante (dernière créée : ici la polyline)
e.fill_mode='off'; // affiche uniquement le contour

delete(e);

// Création d'un nouveau polygone rempli avec la couleur 3
// et son contour dessiné avec la couleur courante
xfpoly(x,y,3);
