refsection.programlisting:

x=sin(2*%pi*(0:4)/5);
y=cos(2*%pi*(0:4)/5);

plot2d(0,0,-1,"010"," ",[-2,-2,2,2])
xpoly(x,y,"lines") // par défaut, close=1
e=gce(); // entité courante (dernière créée : ici la polyline)
set(e,"foreground",5);

e.closed = 'off' // la polyline est maintenant ouverte

refsection.scilab:image:
x=sin(2*%pi*(0:4)/5);
y=cos(2*%pi*(0:4)/5);

plot2d(0,0,-1,"010"," ",[-2,-2,2,2])
xpoly(x,y,"lines")
e=gce();
set(e,"foreground",5)
e.closed = 'off'

refsection.programlisting:
// Avec des marqueurs
plot2d(0,0,1,rect=[0,0,12,10], frameflag=3)
t=[0:4]*2*%pi/5;
x=5+5*cos(t);
y=5+5*sin(t);
xpoly(x,y,"marks")
e=gce();
set(e,"mark_style",2);

