// première sous-fenêtre
xsetech([0,0,1.0,0.5])
plot2d()
// deuxième sous-fenêtre activée 
xsetech([0,0.5,1.0,0.5])
grayplot()
// recupération de l'échelle 
xsetech([0,0,1.0,0.5])
[wrect,frect,logflag,arect]=xgetech();
// recupération de l'échelle 
xsetech([0,0.5,1.0,0.5])
[wrect,frect,logflag,arect]=xgetech();
clf();
