refsection.programlisting:
x=0:0.2:2*%pi;
x1=[sin(x);100*sin(x)];
y1=[cos(x);100*cos(x)];
y1=y1+20*ones(y1);

// ajustando a moldura
clf();a=gca();a.data_bounds=[-100 -100;500 600];

// sem recorte
xsegs(10*x1+200*ones(x1),10*y1+200*ones(y1))
e=gce(); //manipulador da entidade Segs

// desenhando zona de recorte do retângulo
xrect(150,460,100,150)
// ajustando a propriedade clip_box para a entidade Segs
e.clip_box=[150,460,100,150];

// ajustando fronteiras usuais da zona de recorte do retângulo
e.clip_state='clipgrf';
xclip("clipgrf")
// removendo recorte
e.clip_state='off';
