refsection.programlisting:

// Simple example
a=list(1,2,3);
tree_show(a);

// Another example with mlist
str=['a','b','c','d','e','f','g','h'];
n=hypermat([2,2,2],str);
v=hypermat([2,2,2],1:8);
Mm=mlist(['V','name','value'],n,v);
tree_show(Mm);

// Another example with tlist
Mt=tlist(['V','name','value'],['a','b','c'],[1 2 3]);
tree_show(Mt);
