refsection.programlisting:

h=figure();
// 1番の図を作成
uicontrol( h, 'style','text', ...
              'string','scilab is great', ...
              'position',[50 70 100 100], ...
              'fontsize',15);
// 図1に冴えた文字列を指定
figure();
// 図2を作成
uicontrol( 'style','text', ...
           'string','Really great', 'position',[50 70 100 100], 'fontsize',15);
// 図2にテキストを指定
close();
// カレントのグラフィックウインドウ(すなわち図2)を閉じる
close(h);
// 図1を閉じる
