refsection.programlisting:

//create a getdir function based on host
function wd=getdir()
  if getos() == 'Windows' then 
    host('cd>'+TMPDIR+'\path');
  else
    host('pwd>'+TMPDIR+'/path');
  end
  wd=read(TMPDIR+'/path',1,1,'(a)')
endfunction
//call it
wd=getdir()


//how to use timeout parameter
if (getos() <> 'Windows') & ~isfile(SCI+"/bin/scilab") then
    SCI_BIN = strsubst(SCI,'share/scilab','');
else
    SCI_BIN = SCI;
end

if getos() == 'Windows' then
    scilab_bin = SCI_BIN + "\bin\scilex.exe";
else
    scilab_bin = SCI_BIN + "/bin/scilab";
end

i = 1;
while %t
    result = host(scilab_bin + " -e ""sleep(3000);exit(10);""",  i * 1000);
    if result == -1 then
        printf("\texecution cancel after %d milliseconds\n", i * 1000);
        i = i + 1;
    else
        printf("\tnormal exit before %d milliseconds\n", i * 1000);
        break;
    end
end
