refsection.programlisting:

// Example of usage in a buildmacros.sce file 
function buildMacrosAndBlocks()
  script_path = get_absolute_file_path("buildmacros.sce");
  module_path = part(script_path,1:length(script_path)-7)
  tbx_build_macros(NAME,  script_path);

  block_names = ["PlusInt" "Rot3DInt" "IntegralInt" "AbsInt"];

  tbx_build_blocks(module_path, block_names);
  tbx_gen_pal_loader(script_path,TITLE, block_names) //new instruction
endfunction
buildMacrosAndBlocks()
clear buildMacrosAndBlocks;
