para.latex:
\begin{eqnarray}
\begin{array}{l}
\textrm{minimize } c^T \cdot x\\
A_{eq} x = b_{eq}\\
x \geq 0
\end{array}
\end{eqnarray}

refsection.programlisting:

n=10;
p=20;
Aeq=rand(n,p);
c=rand(p,1);
x0=rand(p,1);
beq=Aeq*x0;
xopt=karmarkar(Aeq,beq,c,x0);
// Check constraints
norm(Aeq*xopt-beq)

para.latex:
\begin{eqnarray}
\begin{array}{l}
\textrm{maximize } 20 x_1 + 24 x_2\\
3 x_1 + 6 x_2 \leq 60 \\
4 x_1 + 2 x_2 \leq 32 \\
x_1,x_2 \geq 0
\end{array}
\end{eqnarray}

\begin{eqnarray}
\begin{array}{l}
\textrm{minimize } -20 x_1 - 24 x_2\\
3 x_1 + 6 x_2 + x_3 = 60 \\
4 x_1 + 2 x_2 + x_4 = 32 \\
x_1,x_2,x_3,x_4 \geq 0
\end{array}
\end{eqnarray}

\begin{eqnarray}
\begin{array}{l}
\textrm{maximize } x_1 + x_2\\
2 p x_1 + x_2 \leq 1+p^2, \quad p=0.0, 0.1, 0.2, ..., 1.0\\
x_1,x_2 \geq 0
\end{array}
\end{eqnarray}

refsection.programlisting:

n=11;
Aeq = zeros(n,n+2);
Aeq(:,1) = 2*linspace(0,1,n)';
Aeq(:,2) = ones(11,1);
Aeq(1:n,3:n+2) = eye(n,n);
beq = 1 + linspace(0,1,n)'.^2;
c=[-1;-1;zeros(n,1)];
x0=zeros(n+2,1);
x0(1:2) = [0.1;0.2];
x0(3:n+2)=beq-Aeq(:,1:2)*x0(1:2);
// Plot the constraints
for i = 1 : n
plot(linspace(0,1,100),beq(i)-Aeq(i,1)*linspace(0,1,100),"b-")
end
h=gcf();
h.children.data_bounds = [
0 0
1.1 1.1
];
// Run the optimization
xopt=karmarkar(Aeq,beq,c,x0,[],0.5,[],myoutputfunction);
// Plot the starting and ending points
plot(x0(1),x0(2),"g+")
plot(xopt(1),xopt(2),"k*")
