function y = rosenbrock (x)
  y = 100*(x(2)-x(1)^2)^2 + (1-x(1))^2;
endfunction

s1 = optimsimplex_new ();
simplex = [
-12   0.    0.  
0   1.    0.  
0   0.    1.  
];
s1 = optimsimplex_setall ( s1 , simplex );
s1 = optimsimplex_shrink ( s1 , rosenbrock );
res = optimsimplex_getall ( s1 );
disp(res)

s1 = optimsimplex_destroy(s1);
