refsection.programlisting:

realtimeinit(1/2);//sets time unit to half a second
realtime(0);//sets current date to 0
for k=1:10
  realtime(k);
  mprintf('current time is '+string(k/2)+'sec .\r\n');
end

//next instruction outputs a dot each 2 seconds
realtimeinit(2);
realtime(0);
for k=1:10
  realtime(k);
  mprintf('.\r\n');
end

realtimeinit(1);
realtime(0);
dt=getdate('s');
realtime(10);
getdate('s')-dt

refentry.programlisting:

realtimeinit(1);
realtime(0);
t1 = now()
datevec(t1)
realtime(10);
t1 = now()
datevec(t1)
