refsection.programlisting:
loadScicosLibs();
pal = xcosPal();

sumPath = TMPDIR + "/sum.h5";
bigSomPath = TMPDIR + "/sum.h5";

scs_m = SUM_f("define");
export_to_hdf5(sumPath, "scs_m");
scs_m = BIGSOM("define");
export_to_hdf5(bigSomPath, "scs_m");

pal = xcosPalAddBlock(pal, sumPath);
pal = xcosPalAddBlock(pal, bigSomPath);

xcosPalAdd(pal);
